import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Countdown from 'react-countdown';
import Confetti from 'react-confetti';
import { motion } from 'framer-motion';

const QuizTaker = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(0);
  const [timeSpent, setTimeSpent] = useState(0);
  const [showConfetti, setShowConfetti] = useState(false);
  const [startTime, setStartTime] = useState(Date.now());

  useEffect(() => {
    fetchQuiz();
    // eslint-disable-next-line
  }, [id]);

  const fetchQuiz = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`/api/quizzes/${id}`);
      setQuiz(res.data);
      setError('');
      setStartTime(Date.now());
    } catch (err) {
      setError('Nie znaleziono quizu.');
    }
    setLoading(false);
  };

  const handleAnswer = (qIdx, value) => {
    setAnswers(a => ({ ...a, [qIdx]: value }));
  };

  const handleMultiAnswer = (qIdx, value) => {
    setAnswers(a => ({ ...a, [qIdx]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!quiz) return;
    let correct = 0;
    quiz.questions.forEach((q, idx) => {
      if (q.type === 'single') {
        if (Number(answers[idx]) === q.correctAnswer) correct++;
      } else {
        const arr = Array.isArray(answers[idx]) ? answers[idx].map(Number).sort() : [];
        const correctArr = q.correctAnswers.map(Number).sort();
        if (JSON.stringify(arr) === JSON.stringify(correctArr)) correct++;
      }
    });
    setScore(correct);
    setSubmitted(true);
    setShowConfetti(true);
    const spent = Math.floor((Date.now() - startTime) / 1000);
    setTimeSpent(spent);
    try {
      await axios.post('/api/results', {
        quizId: quiz.id,
        answers,
        timeSpent: spent,
        score: correct,
      });
    } catch {}
    setTimeout(() => setShowConfetti(false), 5000);
  };

  const handleTimeUp = () => {
    if (!submitted) handleSubmit({ preventDefault: () => {} });
  };

  if (loading) return <div className="container">Ładowanie quizu...</div>;
  if (error) return <div className="container" style={{ color: 'red' }}>{error}</div>;
  if (!quiz) return null;

  return (
    <motion.div className="container fade-in" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
      {showConfetti && <Confetti width={window.innerWidth} height={window.innerHeight} recycle={false} />}
      <div className="card" style={{ maxWidth: 800, margin: '40px auto' }}>
        <h2 style={{ marginBottom: 8 }}>{quiz.title}</h2>
        <div style={{ color: '#764ba2', fontWeight: 500, marginBottom: 8 }}>{quiz.category}</div>
        <div style={{ marginBottom: 8 }}>{quiz.description}</div>
        <div style={{ fontSize: 14, color: '#888', marginBottom: 8 }}>
          Poziom: {quiz.difficulty} | Język: {quiz.language}
        </div>
        <div style={{ marginBottom: 16 }}>
          <b>Liczba pytań:</b> {quiz.questions.length}
        </div>
        <div style={{ marginBottom: 16 }}>
          <b>Pozostały czas:</b>{' '}
          <Countdown
            date={Date.now() + (quiz.timeLimit || 300) * 1000}
            onComplete={handleTimeUp}
            renderer={({ minutes, seconds, completed }) =>
              completed ? (
                <span style={{ color: 'red' }}>Czas minął!</span>
              ) : (
                <span>
                  {minutes}:{seconds.toString().padStart(2, '0')}
                </span>
              )
            }
          />
        </div>
        <form onSubmit={handleSubmit}>
          {quiz.questions.map((q, idx) => (
            <div key={q.id} className="card" style={{ marginBottom: 16, background: '#f8f9fa' }}>
              <div style={{ marginBottom: 8 }}>
                <b>Pytanie {idx + 1}:</b> {q.question}
              </div>
              {q.type === 'single' ? (
                <div>
                  {q.options.map((opt, optIdx) => (
                    <label key={optIdx} style={{ display: 'block', marginBottom: 6 }}>
                      <input
                        type="radio"
                        name={`q${idx}`}
                        value={optIdx}
                        checked={answers[idx] === String(optIdx) || answers[idx] === optIdx}
                        onChange={e => handleAnswer(idx, e.target.value)}
                        disabled={submitted}
                        required
                      />{' '}
                      {opt}
                    </label>
                  ))}
                </div>
              ) : (
                <div>
                  {q.options.map((opt, optIdx) => (
                    <label key={optIdx} style={{ display: 'block', marginBottom: 6 }}>
                      <input
                        type="checkbox"
                        name={`q${idx}`}
                        value={optIdx}
                        checked={Array.isArray(answers[idx]) && answers[idx].includes(optIdx)}
                        onChange={e => {
                          let arr = Array.isArray(answers[idx]) ? [...answers[idx]] : [];
                          if (e.target.checked) arr.push(optIdx);
                          else arr = arr.filter(v => v !== optIdx);
                          handleMultiAnswer(idx, arr);
                        }}
                        disabled={submitted}
                      />{' '}
                      {opt}
                    </label>
                  ))}
                </div>
              )}
            </div>
          ))}
          {!submitted && (
            <button className="btn btn-primary" type="submit" style={{ width: '100%' }}>
              Zakończ i sprawdź wynik
            </button>
          )}
        </form>
        {submitted && (
          <div style={{ marginTop: 24, textAlign: 'center' }}>
            <h3>Twój wynik: {score} / {quiz.questions.length}</h3>
            <div>Czas rozwiązania: {timeSpent} sekund</div>
            <button className="btn btn-secondary" style={{ marginTop: 16 }} onClick={() => navigate('/results')}>
              Zobacz swoje wyniki
            </button>
          </div>
        )}
      </div>
    </motion.div>
  );
};

export default QuizTaker;
