import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { FiCheck, FiX, FiArrowLeft, FiBarChart2, FiClock, FiUsers } from 'react-icons/fi';
import axios from 'axios';
import { useTranslation } from '../context/TranslationContext';
import { useAuth } from '../context/AuthContext';
import { useColors } from '../context/ColorContext';

// Konfiguracja axios
axios.defaults.baseURL = 'http://localhost:5000';

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  padding-top: 90px;
`;

const ResultsCard = styled(motion.div)`
  background: white;
  border-radius: 16px;
  padding: 32px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  border: 1px solid ${props => props.colors.primary}10;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 32px;
`;

const Title = styled.h1`
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 16px;
  color: ${props => props.colors.text};
`;

const ScoreSection = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 24px;
  margin-bottom: 32px;
`;

const ScoreCard = styled.div`
  background: ${props => props.isUser ? props.gradient : 'linear-gradient(135deg, #28a745 0%, #20c997 100%)'};
  color: white;
  padding: 24px;
  border-radius: 12px;
  text-align: center;
`;

const ScoreTitle = styled.h3`
  font-size: 1.1rem;
  font-weight: 600;
  margin-bottom: 8px;
`;

const ScoreValue = styled.div`
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 8px;
`;

const ScoreLabel = styled.div`
  font-size: 0.9rem;
  opacity: 0.9;
`;

const StatsSection = styled.div`
  background: ${props => props.colors.background};
  padding: 24px;
  border-radius: 12px;
  margin-bottom: 32px;
`;

const StatsTitle = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  margin-bottom: 16px;
  color: ${props => props.colors.text};
  text-align: center;
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 16px;
`;

const StatItem = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 16px;
  background: white;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
`;

const StatIcon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: ${props => props.color || props.colors.primary};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
`;

const StatContent = styled.div`
  flex: 1;
`;

const StatValue = styled.div`
  font-size: 1.2rem;
  font-weight: 600;
  color: ${props => props.colors.text};
`;

const StatLabel = styled.div`
  font-size: 0.9rem;
  color: ${props => props.colors.textLight};
`;

const ActionButtons = styled.div`
  display: flex;
  gap: 16px;
  justify-content: center;
`;

const ActionButton = styled.button`
  padding: 12px 24px;
  border: none;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;

  &.primary {
    background: ${props => props.gradient};
    color: white;
    box-shadow: 0 2px 8px ${props => props.colors.primary}30;

    &:hover {
      transform: translateY(-2px);
      box-shadow: 0 4px 12px ${props => props.colors.primary}50;
    }
  }

  &.secondary {
    background: ${props => props.colors.background};
    color: ${props => props.colors.text};
    border: 2px solid #e9ecef;

    &:hover {
      background: #e9ecef;
      transform: translateY(-2px);
    }
  }
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 40px;
  color: ${props => props.colors.textLight};
  font-size: 1.1rem;
`;

const ErrorMessage = styled.div`
  text-align: center;
  padding: 40px;
  color: #dc3545;
  font-size: 1.1rem;
`;

const QuizResults = () => {
  const { id } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const { user } = useAuth();
  const { t } = useTranslation();
  const { colors } = useColors();
  
  const [quiz, setQuiz] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // Dane z poprzedniej strony (QuizTaker)
  const userResult = location.state;

  useEffect(() => {
    fetchQuizAndStats();
  }, [id]);

  const fetchQuizAndStats = async () => {
    try {
      setLoading(true);
      
      // Pobierz quiz
      const quizResponse = await axios.get(`/api/quizzes/${id}`);
      setQuiz(quizResponse.data);
      
      // Pobierz statystyki quizu
      const statsResponse = await axios.get(`/api/quizzes/${id}/stats`);
      setStats(statsResponse.data);
      
      setError(null);
    } catch (err) {
      setError(t('Błąd podczas pobierania wyników.'));
      console.error('Error fetching quiz results:', err);
    } finally {
      setLoading(false);
    }
  };

  const getScoreColor = (percentage) => {
    if (percentage >= 80) return '#28a745';
    if (percentage >= 60) return '#ffc107';
    return '#dc3545';
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (loading) {
    return (
      <Container>
        <LoadingMessage>{t('Ładowanie wyników...')}</LoadingMessage>
      </Container>
    );
  }

  if (error || !quiz || !userResult) {
    return (
      <Container>
        <ErrorMessage>{error || t('Nie znaleziono wyników.')}</ErrorMessage>
      </Container>
    );
  }

  return (
    <Container>
      <ResultsCard
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        colors={colors}
      >
        <Header>
          <Title colors={colors}>{quiz.title}</Title>
        </Header>

        <ScoreSection>
          <ScoreCard isUser={true} gradient={colors.gradient}>
            <ScoreTitle>{t('Twój wynik')}</ScoreTitle>
            <ScoreValue style={{ color: getScoreColor(userResult.percentage) }}>
              {userResult.percentage}%
            </ScoreValue>
            <ScoreLabel>{userResult.score} / {userResult.totalQuestions} {t('pytania')}</ScoreLabel>
          </ScoreCard>
          
          <ScoreCard isUser={false} gradient={colors.gradient}>
            <ScoreTitle>{t('Średni wynik')}</ScoreTitle>
            <ScoreValue>
              {stats?.averageScore || 0}%
            </ScoreValue>
            <ScoreLabel>{t('Wszystkich użytkowników')}</ScoreLabel>
          </ScoreCard>
        </ScoreSection>

        <StatsSection colors={colors}>
          <StatsTitle colors={colors}>{t('Statystyki quizu')}</StatsTitle>
          <StatsGrid>
            <StatItem>
              <StatIcon color="#667eea" colors={colors}>
                <FiUsers />
              </StatIcon>
              <StatContent>
                <StatValue colors={colors}>{stats?.totalAttempts || 0}</StatValue>
                <StatLabel colors={colors}>{t('Liczba podejść')}</StatLabel>
              </StatContent>
            </StatItem>
            
            <StatItem>
              <StatIcon color="#28a745" colors={colors}>
                <FiBarChart2 />
              </StatIcon>
              <StatContent>
                <StatValue colors={colors}>{stats?.averageScore || 0}%</StatValue>
                <StatLabel colors={colors}>{t('Średni wynik')}</StatLabel>
              </StatContent>
            </StatItem>
            
            <StatItem>
              <StatIcon color="#ffc107" colors={colors}>
                <FiClock />
              </StatIcon>
              <StatContent>
                <StatValue colors={colors}>{formatTime(userResult.timeSpent)}</StatValue>
                <StatLabel colors={colors}>{t('Twój czas')}</StatLabel>
              </StatContent>
            </StatItem>
            
            <StatItem>
              <StatIcon color="#20c997" colors={colors}>
                <FiClock />
              </StatIcon>
              <StatContent>
                <StatValue colors={colors}>{formatTime(stats?.averageTime || 0)}</StatValue>
                <StatLabel colors={colors}>{t('Średni czas')}</StatLabel>
              </StatContent>
            </StatItem>
          </StatsGrid>
        </StatsSection>

        <ActionButtons>
          <ActionButton 
            className="secondary"
            onClick={() => navigate('/quizzes')}
            colors={colors}
          >
            <FiArrowLeft />
            {t('Powrót do quizów')}
          </ActionButton>
          
          <ActionButton 
            className="primary"
            onClick={() => navigate(`/quiz/${id}`)}
            gradient={colors.gradient}
            colors={colors}
          >
            <FiBarChart2 />
            {t('Zobacz szczegóły quizu')}
          </ActionButton>
        </ActionButtons>
      </ResultsCard>
    </Container>
  );
};

export default QuizResults;
