import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useTranslation } from '../context/TranslationContext';

const defaultQuestion = () => ({
  question: '',
  type: 'single',
  options: ['', '', '', ''],
  correctAnswer: 0,
  correctAnswers: [0],
});

const QuizCreator = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [difficulty, setDifficulty] = useState('medium');
  const [language, setLanguage] = useState('pl');
  const [questions, setQuestions] = useState([defaultQuestion()]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { t, updateKey } = useTranslation();

  const handleQuestionChange = (idx, field, value) => {
    setQuestions(questions =>
      questions.map((q, i) =>
        i === idx ? { ...q, [field]: value } : q
      )
    );
  };

  const handleOptionChange = (qIdx, optIdx, value) => {
    setQuestions(questions =>
      questions.map((q, i) =>
        i === qIdx
          ? { ...q, options: q.options.map((opt, j) => (j === optIdx ? value : opt)) }
          : q
      )
    );
  };

  const handleCorrectAnswerChange = (qIdx, value) => {
    setQuestions(questions =>
      questions.map((q, i) =>
        i === qIdx ? { ...q, correctAnswer: Number(value) } : q
      )
    );
  };

  const handleCorrectAnswersChange = (qIdx, value) => {
    setQuestions(questions =>
      questions.map((q, i) =>
        i === qIdx ? { ...q, correctAnswers: value } : q
      )
    );
  };

  const addQuestion = () => {
    setQuestions([...questions, defaultQuestion()]);
  };

  const removeQuestion = (idx) => {
    setQuestions(questions => questions.filter((_, i) => i !== idx));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const formattedQuestions = questions.map(q =>
        q.type === 'single'
          ? { ...q, correctAnswer: Number(q.correctAnswer) }
          : { ...q, correctAnswers: q.correctAnswers.map(Number) }
      );
      const res = await axios.post('/api/quizzes', {
        title,
        description,
        category,
        difficulty,
        language,
        questions: formattedQuestions,
      });
      navigate(`/quizzes/${res.data.id}`);
    } catch (err) {
      setError('Błąd podczas tworzenia quizu.');
    }
    setLoading(false);
  };

  return (
    <motion.div className="container fade-in" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
      <div className="card" style={{ maxWidth: 800, margin: '40px auto' }}>
        <h2 style={{ marginBottom: 24 }}>{t('createQuiz')}</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">{t('quizTitle')}</label>
            <input className="form-input" value={title} onChange={e => setTitle(e.target.value)} required />
          </div>
          <div className="form-group">
            <label className="form-label">{t('quizDescription')}</label>
            <input className="form-input" value={description} onChange={e => setDescription(e.target.value)} required />
          </div>
          <div className="form-group">
            <label className="form-label">{t('category')}</label>
            <input className="form-input" value={category} onChange={e => setCategory(e.target.value)} required />
          </div>
          <div className="form-group">
            <label className="form-label">{t('difficulty')}</label>
            <select className="form-select" value={difficulty} onChange={e => setDifficulty(e.target.value)}>
              <option value="easy">{t('easy')}</option>
              <option value="medium">{t('medium')}</option>
              <option value="hard">{t('hard')}</option>
            </select>
          </div>
          <div className="form-group">
            <label className="form-label">{t('language')}</label>
            <select className="form-select" value={language} onChange={e => setLanguage(e.target.value)}>
              <option value="pl">{t('polish')}</option>
              <option value="en">{t('english')}</option>
            </select>
          </div>
          <h3 style={{ margin: '24px 0 12px 0' }}>{t('questions')}</h3>
          {questions.map((q, idx) => (
            <div key={idx} className="card" style={{ marginBottom: 16, background: '#f8f9fa' }}>
              <div className="form-group">
                <label className="form-label">{t('questionContent')}</label>
                <input className="form-input" value={q.question} onChange={e => handleQuestionChange(idx, 'question', e.target.value)} required />
              </div>
              <div className="form-group">
                <label className="form-label">{t('questionType')}</label>
                <select className="form-select" value={q.type} onChange={e => handleQuestionChange(idx, 'type', e.target.value)}>
                  <option value="single">{t('singleChoice')}</option>
                  <option value="multiple">{t('multipleChoice')}</option>
                </select>
              </div>
              <div className="form-group">
                <label className="form-label">{t('options')}</label>
                {q.options.map((opt, optIdx) => (
                  <input
                    key={optIdx}
                    className="form-input"
                    style={{ marginBottom: 8 }}
                    value={opt}
                    onChange={e => handleOptionChange(idx, optIdx, e.target.value)}
                    required
                    placeholder={`${t('answer')} ${optIdx + 1}`}
                  />
                ))}
              </div>
              {q.type === 'single' ? (
                <div className="form-group">
                  <label className="form-label">{t('correctAnswer')}</label>
                  <select className="form-select" value={q.correctAnswer} onChange={e => handleCorrectAnswerChange(idx, e.target.value)}>
                    {q.options.map((opt, optIdx) => (
                      <option key={optIdx} value={optIdx}>{opt || `${t('answer')} ${optIdx + 1}`}</option>
                    ))}
                  </select>
                </div>
              ) : (
                <div className="form-group">
                  <label className="form-label">{t('correctAnswers')}</label>
                  <select
                    className="form-select"
                    multiple
                    value={q.correctAnswers}
                    onChange={e => {
                      const selected = Array.from(e.target.selectedOptions, option => Number(option.value));
                      handleCorrectAnswersChange(idx, selected);
                    }}
                  >
                    {q.options.map((opt, optIdx) => (
                      <option key={optIdx} value={optIdx}>{opt || `${t('answer')} ${optIdx + 1}`}</option>
                    ))}
                  </select>
                </div>
              )}
              <button type="button" className="btn btn-danger" onClick={() => removeQuestion(idx)} disabled={questions.length === 1}>
                {t('removeQuestion')}
              </button>
            </div>
          ))}
          <button type="button" className="btn btn-secondary" onClick={addQuestion} style={{ marginBottom: 24 }}>
            {t('addQuestion')}
          </button>
          {error && <div style={{ color: 'red', marginBottom: 12 }}>{error}</div>}
          <button className="btn btn-primary" type="submit" disabled={loading} style={{ width: '100%' }}>
            {loading ? t('creating') : t('createQuiz')}
          </button>
        </form>
      </div>
    </motion.div>
  );
};

export default QuizCreator;
