import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';

const Results = () => {
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchResults();
    // eslint-disable-next-line
  }, []);

  const fetchResults = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/results');
      setResults(res.data);
    } catch {
      setResults([]);
    }
    setLoading(false);
  };

  return (
    <motion.div className="container fade-in" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
      <h2 style={{ margin: '32px 0 24px 0', textAlign: 'center' }}>Twoje wyniki</h2>
      {loading ? (
        <div>Ładowanie wyników...</div>
      ) : results.length === 0 ? (
        <div>Brak wyników do wyświetlenia.</div>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: 24, justifyContent: 'center' }}>
          {results.map(result => (
            <motion.div
              className="card fade-in"
              key={result.id}
              whileHover={{ scale: 1.04 }}
              style={{ minWidth: 280, maxWidth: 340 }}
            >
              <div style={{ marginBottom: 8 }}><b>Quiz:</b> {result.quizId}</div>
              <div style={{ marginBottom: 8 }}><b>Wynik:</b> {result.score}</div>
              <div style={{ marginBottom: 8 }}><b>Czas:</b> {result.timeSpent} sekund</div>
              <div style={{ marginBottom: 8 }}><b>Data:</b> {new Date(result.completedAt).toLocaleString()}</div>
            </motion.div>
          ))}
        </div>
      )}
    </motion.div>
  );
};

export default Results;
