import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { FiBook, FiPlus } from 'react-icons/fi';
import { useTranslation } from '../context/TranslationContext';
import { useAuth } from '../context/AuthContext';

const HomeContainer = styled.div`
  min-height: 100vh;
  padding-top: 70px;
`;

const Hero = styled(motion.section)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 80vh;
  text-align: center;
  color: #333;
  padding: 40px 20px;
  background: linear-gradient(135deg, rgba(102, 126, 234, 0.05) 0%, rgba(118, 75, 162, 0.05) 100%);
`;

const Title = styled.h1`
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 800;
  margin-bottom: 20px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.2;
`;

const Subtitle = styled.p`
  font-size: clamp(1.1rem, 2.5vw, 1.4rem);
  margin-bottom: 40px;
  color: #555;
  max-width: 800px;
  line-height: 1.6;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  flex-wrap: wrap;
`;

const HeroButton = styled(Link)`
  padding: 16px 32px;
  border-radius: 12px;
  font-size: 1.1rem;
  font-weight: 600;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  text-decoration: none;
  box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;
  
  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 12px 35px rgba(102, 126, 234, 0.4);
    color: white;
  }
`;

const Home = () => {
  const { t, updateKey } = useTranslation();
  const { user } = useAuth();

  return (
    <HomeContainer>
      <Hero initial={{ opacity: 0, y: 40 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.7 }} key={updateKey}>
        <Title>QuizMaster</Title>
        <Subtitle>
          {t('Nowoczesna platforma e-learningowa do rozwiązywania i tworzenia quizów.')}<br />
          {t('Ucz się, sprawdzaj wiedzę i rywalizuj z innymi!')}<br />
          <span style={{ color: '#764ba2', fontWeight: 600 }}>{t('Dostępne po polsku i angielsku.')}</span>
        </Subtitle>
        <ButtonGroup>
          <HeroButton to="/quizzes">
            <FiBook />
            {t('Przeglądaj quizy')}
          </HeroButton>
          {user ? (
            <HeroButton to="/create-quiz">
              <FiPlus />
              {t('Utwórz własny quiz')}
            </HeroButton>
          ) : (
            <HeroButton to="/register">
              <FiPlus />
              {t('Zarejestruj się')}
            </HeroButton>
          )}
        </ButtonGroup>
      </Hero>
    </HomeContainer>
  );
};

export default Home;
