import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useTranslation } from '../context/TranslationContext';

const defaultQuestion = () => ({
  question: '',
  type: 'single',
  options: ['', '', '', ''],
  correctAnswer: 0,
  correctAnswers: [0],
});

const QuizCreator = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('Ogólne');
  const [difficulty, setDifficulty] = useState('medium');
  const [language, setLanguage] = useState('pl');
  const [questions, setQuestions] = useState([defaultQuestion()]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [previewLanguage, setPreviewLanguage] = useState('pl');
  const [previewData, setPreviewData] = useState(null);
  
  const navigate = useNavigate();
  const { t, updateKey, translateQuiz } = useTranslation();

  const categories = [
    'Ogólne', 'Geografia', 'Historia', 'Matematyka', 'Fizyka', 'Chemia', 
    'Biologia', 'Informatyka', 'Literatura', 'Sztuka', 'Muzyka', 'Sport', 
    'Nauka', 'Technologia', 'Medycyna', 'Prawo', 'Ekonomia', 'Filozofia', 
    'Psychologia', 'Sociologia', 'Polityka', 'Religia', 'Języki', 'Inne'
  ];

  const difficulties = [
    { value: 'easy', label: t('Łatwy') },
    { value: 'medium', label: t('Średni') },
    { value: 'hard', label: t('Trudny') }
  ];

  const languages = [
    { value: 'pl', label: t('Polski') },
    { value: 'en', label: t('Angielski') }
  ];

  const previewLanguages = [
    { value: 'pl', label: 'Polski' },
    { value: 'en', label: 'English' },
    { value: 'de', label: 'Deutsch' },
    { value: 'es', label: 'Español' },
    { value: 'fr', label: 'Français' }
  ];

  const handleQuestionChange = (idx, field, value) => {
    setQuestions(questions =>
      questions.map((q, i) =>
        i === idx ? { ...q, [field]: value } : q
      )
    );
  };

  const handleOptionChange = (qIdx, optIdx, value) => {
    setQuestions(questions =>
      questions.map((q, i) =>
        i === qIdx
          ? { ...q, options: q.options.map((opt, j) => (j === optIdx ? value : opt)) }
          : q
      )
    );
  };

  const handleCorrectAnswerChange = (qIdx, value) => {
    setQuestions(questions =>
      questions.map((q, i) =>
        i === qIdx ? { ...q, correctAnswer: Number(value) } : q
      )
    );
  };

  const handleCorrectAnswersChange = (qIdx, value) => {
    setQuestions(questions =>
      questions.map((q, i) =>
        i === qIdx ? { ...q, correctAnswers: value } : q
      )
    );
  };

  const addQuestion = () => {
    setQuestions([...questions, defaultQuestion()]);
  };

  const removeQuestion = (idx) => {
    if (questions.length > 1) {
      setQuestions(questions => questions.filter((_, i) => i !== idx));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const formattedQuestions = questions.map(q =>
        q.type === 'single'
          ? { ...q, correctAnswer: Number(q.correctAnswer) }
          : { ...q, correctAnswers: q.correctAnswers.map(Number) }
      );
      const res = await axios.post('/api/quizzes', {
        title,
        description,
        category,
        difficulty,
        language,
        questions: formattedQuestions,
      });
      navigate(`/quizzes/${res.data.id}`);
    } catch (err) {
      setError(t('Błąd podczas tworzenia quizu.'));
    }
    setLoading(false);
  };

  // Generuj podgląd quizu w wybranym języku
  const generatePreview = async () => {
    if (previewLanguage === 'pl') return null;
    
    const previewQuiz = {
      title,
      description,
      category,
      language,
      questions: questions.map(q => ({
        question: q.question,
        options: q.options
      }))
    };
    
    try {
      return await translateQuiz(previewQuiz, previewLanguage);
    } catch (error) {
      console.error('Preview translation error:', error);
      return previewQuiz;
    }
  };

  useEffect(() => {
    const updatePreview = async () => {
      if (previewLanguage !== 'pl') {
        const preview = await generatePreview();
        setPreviewData(preview);
      } else {
        setPreviewData(null);
      }
    };
    updatePreview();
  }, [previewLanguage, title, description, category, language, questions]);

  return (
    <motion.div className="container fade-in" initial={{ opacity: 0 }} animate={{ opacity: 1 }} key={updateKey}>
      <div className="card" style={{ maxWidth: 1200, margin: '40px auto' }}>
        <h2 style={{ marginBottom: 24 }}>{t('Stwórz nowy quiz')}</h2>
        
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '40px' }}>
          {/* Formularz tworzenia */}
          <div>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">{t('Tytuł quizu')}</label>
                <input className="form-input" value={title} onChange={e => setTitle(e.target.value)} required />
              </div>
              <div className="form-group">
                <label className="form-label">{t('Opis quizu')}</label>
                <textarea 
                  className="form-input" 
                  value={description} 
                  onChange={e => setDescription(e.target.value)} 
                  rows="3"
                  required 
                />
              </div>
              <div className="form-group">
                <label className="form-label">{t('Kategoria')}</label>
                <select className="form-select" value={category} onChange={e => setCategory(e.target.value)} required>
                  {categories.map(cat => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label className="form-label">{t('Poziom trudności')}</label>
                <select className="form-select" value={difficulty} onChange={e => setDifficulty(e.target.value)} required>
                  {difficulties.map(diff => (
                    <option key={diff.value} value={diff.value}>{diff.label}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label className="form-label">{t('Język quizu')}</label>
                <select className="form-select" value={language} onChange={e => setLanguage(e.target.value)} required>
                  {languages.map(lang => (
                    <option key={lang.value} value={lang.value}>{lang.label}</option>
                  ))}
                </select>
              </div>
              
              <h3 style={{ margin: '24px 0 12px 0' }}>{t('Pytania')}</h3>
              {questions.map((q, idx) => (
                <div key={idx} className="card" style={{ marginBottom: 16, background: '#f8f9fa' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 12 }}>
                    <h4 style={{ margin: 0 }}>{t('Pytanie')} {idx + 1}</h4>
                    {questions.length > 1 && (
                      <button
                        type="button"
                        onClick={() => removeQuestion(idx)}
                        style={{ background: 'none', border: 'none', color: '#dc3545', cursor: 'pointer' }}
                      >
                        {t('Usuń pytanie')}
                      </button>
                    )}
                  </div>
                  
                  <div className="form-group">
                    <label className="form-label">{t('Pytanie')}</label>
                    <input className="form-input" value={q.question} onChange={e => handleQuestionChange(idx, 'question', e.target.value)} required />
                  </div>
                  <div className="form-group">
                    <label className="form-label">{t('Typ pytania')}</label>
                    <select className="form-select" value={q.type} onChange={e => handleQuestionChange(idx, 'type', e.target.value)} required>
                      <option value="single">{t('Pojedynczy wybór')}</option>
                      <option value="multiple">{t('Wielokrotny wybór')}</option>
                    </select>
                  </div>
                  
                  <div className="form-group">
                    <label className="form-label">{t('Opcje')}</label>
                    {q.options.map((opt, optIdx) => (
                      <div key={optIdx} style={{ display: 'flex', alignItems: 'center', marginBottom: 8 }}>
                        <input
                          type={q.type === 'single' ? 'radio' : 'checkbox'}
                          name={`correct-${idx}`}
                          value={optIdx}
                          checked={
                            q.type === 'single'
                              ? q.correctAnswer === optIdx
                              : q.correctAnswers.includes(optIdx)
                          }
                          onChange={(e) => {
                            if (q.type === 'single') {
                              handleCorrectAnswerChange(idx, e.target.value);
                            } else {
                              const value = Number(e.target.value);
                              const current = q.correctAnswers || [];
                              const newAnswers = e.target.checked
                                ? [...current, value]
                                : current.filter(a => a !== value);
                              handleCorrectAnswersChange(idx, newAnswers);
                            }
                          }}
                          style={{ marginRight: 8 }}
                        />
                        <input
                          className="form-input"
                          value={opt}
                          onChange={e => handleOptionChange(idx, optIdx, e.target.value)}
                          placeholder={`${t('Opcja')} ${optIdx + 1}`}
                          required
                        />
                      </div>
                    ))}
                  </div>
                </div>
              ))}
              
              <button
                type="button"
                onClick={addQuestion}
                style={{ 
                  background: '#28a745', 
                  color: 'white', 
                  border: 'none', 
                  padding: '8px 16px', 
                  borderRadius: 4, 
                  cursor: 'pointer',
                  marginBottom: 16
                }}
              >
                {t('Dodaj pytanie')}
              </button>
              
              {error && (
                <div style={{ background: '#f8d7da', color: '#721c24', padding: 12, borderRadius: 4, marginBottom: 16 }}>
                  {error}
                </div>
              )}
              
              <button
                type="submit"
                disabled={loading}
                className="btn btn-primary"
                style={{ width: '100%' }}
              >
                {loading ? t('Tworzenie...') : t('Utwórz quiz')}
              </button>
            </form>
          </div>

          {/* Podgląd quizu */}
          <div>
            <div className="form-group">
              <label className="form-label">Podgląd w języku:</label>
              <select 
                className="form-select" 
                value={previewLanguage} 
                onChange={(e) => setPreviewLanguage(e.target.value)}
              >
                {previewLanguages.map(lang => (
                  <option key={lang.value} value={lang.value}>{lang.label}</option>
                ))}
              </select>
            </div>
            
            <div className="card" style={{ background: '#f8f9fa', marginTop: 16 }}>
              <h3 style={{ marginBottom: 16 }}>Podgląd quizu</h3>
              
              {previewData ? (
                <div>
                  <div style={{ marginBottom: 16 }}>
                    <strong>Tytuł:</strong>
                    <p>{previewData.title || title || 'Brak tytułu'}</p>
                  </div>
                  
                  <div style={{ marginBottom: 16 }}>
                    <strong>Opis:</strong>
                    <p>{previewData.description || description || 'Brak opisu'}</p>
                  </div>
                  
                  <div style={{ marginBottom: 16 }}>
                    <strong>Kategoria:</strong>
                    <p>{previewData.category || category}</p>
                  </div>
                  
                  <div>
                    <strong>Pytania:</strong>
                    {previewData.questions?.map((q, index) => (
                      <div key={index} style={{ marginTop: 12, padding: 12, background: 'white', borderRadius: 4 }}>
                        <p><strong>{index + 1}.</strong> {q.question}</p>
                        <div style={{ marginLeft: 16 }}>
                          {q.options.map((opt, optIndex) => (
                            <p key={optIndex} style={{ margin: 4, fontSize: '14px' }}>
                              {String.fromCharCode(65 + optIndex)}. {opt}
                            </p>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ) : (
                <div>
                  <div style={{ marginBottom: 16 }}>
                    <strong>Tytuł:</strong>
                    <p>{title || 'Brak tytułu'}</p>
                  </div>
                  
                  <div style={{ marginBottom: 16 }}>
                    <strong>Opis:</strong>
                    <p>{description || 'Brak opisu'}</p>
                  </div>
                  
                  <div style={{ marginBottom: 16 }}>
                    <strong>Kategoria:</strong>
                    <p>{category}</p>
                  </div>
                  
                  <div>
                    <strong>Pytania:</strong>
                    {questions.map((q, index) => (
                      <div key={index} style={{ marginTop: 12, padding: 12, background: 'white', borderRadius: 4 }}>
                        <p><strong>{index + 1}.</strong> {q.question || 'Brak pytania'}</p>
                        <div style={{ marginLeft: 16 }}>
                          {q.options.map((opt, optIndex) => (
                            <p key={optIndex} style={{ margin: 4, fontSize: '14px' }}>
                              {String.fromCharCode(65 + optIndex)}. {opt || 'Brak opcji'}
                            </p>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default QuizCreator;
