import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { FiBook, FiPlus, FiTrendingUp, FiUsers, FiAward, FiGlobe } from 'react-icons/fi';
import { useTranslation } from '../context/TranslationContext';
import { useAuth } from '../context/AuthContext';

const HomeContainer = styled.div`
  min-height: 100vh;
  padding-top: 70px;
`;

const Hero = styled(motion.section)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 60vh;
  text-align: center;
  color: #333;
  padding: 40px 20px;
  background: linear-gradient(135deg, rgba(102, 126, 234, 0.05) 0%, rgba(118, 75, 162, 0.05) 100%);
`;

const Title = styled.h1`
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 800;
  margin-bottom: 20px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.2;
`;

const Subtitle = styled.p`
  font-size: clamp(1.1rem, 2.5vw, 1.4rem);
  margin-bottom: 40px;
  color: #555;
  max-width: 800px;
  line-height: 1.6;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  flex-wrap: wrap;
  margin-bottom: 60px;
`;

const HeroButton = styled(Link)`
  padding: 16px 32px;
  border-radius: 12px;
  font-size: 1.1rem;
  font-weight: 600;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  text-decoration: none;
  box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;
  
  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 12px 35px rgba(102, 126, 234, 0.4);
    color: white;
  }
`;

const FeaturesSection = styled.section`
  padding: 80px 20px;
  background: white;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionTitle = styled.h2`
  text-align: center;
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 60px;
  color: #333;
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 40px;
  margin-bottom: 60px;
`;

const FeatureCard = styled(motion.div)`
  background: white;
  padding: 40px 30px;
  border-radius: 16px;
  text-align: center;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  border: 1px solid rgba(102, 126, 234, 0.1);

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
  }
`;

const FeatureIcon = styled.div`
  width: 80px;
  height: 80px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 20px;
  color: white;
  font-size: 2rem;
`;

const FeatureTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 15px;
  color: #333;
`;

const FeatureDescription = styled.p`
  color: #666;
  line-height: 1.6;
  font-size: 1rem;
`;

const StatsSection = styled.section`
  padding: 60px 20px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 40px;
  max-width: 1200px;
  margin: 0 auto;
`;

const StatCard = styled(motion.div)`
  text-align: center;
`;

const StatNumber = styled.div`
  font-size: 3rem;
  font-weight: 800;
  margin-bottom: 10px;
`;

const StatLabel = styled.div`
  font-size: 1.1rem;
  opacity: 0.9;
`;

const CTAButton = styled(Link)`
  display: inline-block;
  padding: 18px 36px;
  background: white;
  color: #667eea;
  text-decoration: none;
  border-radius: 12px;
  font-weight: 600;
  font-size: 1.1rem;
  margin-top: 40px;
  transition: all 0.3s ease;
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 12px 35px rgba(0, 0, 0, 0.3);
    color: #667eea;
  }
`;

const Home = () => {
  const { t, updateKey } = useTranslation();
  const { user } = useAuth();

  const features = [
    {
      icon: <FiBook />,
      title: 'Przeglądaj Quizy',
      description: 'Odkryj tysiące quizów z różnych kategorii i poziomów trudności. Od geografii po programowanie - znajdziesz coś dla siebie!'
    },
    {
      icon: <FiPlus />,
      title: 'Twórz Własne Quizy',
      description: 'Stwórz własne quizy i udostępnij je innym. Dodawaj pytania, ustawiaj czas i śledź wyniki uczestników.'
    },
    {
      icon: <FiTrendingUp />,
      title: 'Śledź Postępy',
      description: 'Analizuj swoje wyniki, sprawdzaj statystyki i obserwuj swój rozwój. Systematycznie poprawiaj swoje umiejętności.'
    },
    {
      icon: <FiUsers />,
      title: 'Społeczność',
      description: 'Dołącz do społeczności uczących się. Rywalizuj z innymi, dziel się wiedzą i motywuj się nawzajem.'
    },
    {
      icon: <FiAward />,
      title: 'Osiągnięcia',
      description: 'Zdobywaj punkty, odznaki i rankingi. Sprawdź jak wypadasz na tle innych użytkowników platformy.'
    },
    {
      icon: <FiGlobe />,
      title: 'Wielojęzyczność',
      description: 'Platforma dostępna w wielu językach. Ucz się w swoim języku lub ćwicz znajomość obcych języków.'
    }
  ];

  return (
    <HomeContainer>
      <Hero initial={{ opacity: 0, y: 40 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.7 }} key={updateKey}>
        <Title>QuizMaster</Title>
        <Subtitle>
          {t('Nowoczesna platforma e-learningowa do rozwiązywania i tworzenia quizów.')}<br />
          {t('Ucz się, sprawdzaj wiedzę i rywalizuj z innymi!')}<br />
          <span style={{ color: '#764ba2', fontWeight: 600 }}>{t('Dostępne po polsku i angielsku.')}</span>
        </Subtitle>
        <ButtonGroup>
          <HeroButton to="/quizzes">
            <FiBook />
            {t('Przeglądaj quizy')}
          </HeroButton>
          {user ? (
            <HeroButton to="/create-quiz">
              <FiPlus />
              {t('Utwórz własny quiz')}
            </HeroButton>
          ) : (
            <HeroButton to="/register">
              <FiPlus />
              {t('Zarejestruj się')}
            </HeroButton>
          )}
        </ButtonGroup>
      </Hero>

      <FeaturesSection>
        <Container>
          <SectionTitle>Dlaczego QuizMaster?</SectionTitle>
          <FeaturesGrid>
            {features.map((feature, index) => (
              <FeatureCard
                key={index}
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
              >
                <FeatureIcon>{feature.icon}</FeatureIcon>
                <FeatureTitle>{feature.title}</FeatureTitle>
                <FeatureDescription>{feature.description}</FeatureDescription>
              </FeatureCard>
            ))}
          </FeaturesGrid>
        </Container>
      </FeaturesSection>

      <StatsSection>
        <Container>
          <SectionTitle style={{ color: 'white', marginBottom: '40px' }}>Statystyki Platformy</SectionTitle>
          <StatsGrid>
            <StatCard
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5, delay: 0.1 }}
            >
              <StatNumber>1000+</StatNumber>
              <StatLabel>Quizów</StatLabel>
            </StatCard>
            <StatCard
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              <StatNumber>50+</StatNumber>
              <StatLabel>Kategorii</StatLabel>
            </StatCard>
            <StatCard
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5, delay: 0.3 }}
            >
              <StatNumber>10k+</StatNumber>
              <StatLabel>Użytkowników</StatLabel>
            </StatCard>
            <StatCard
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5, delay: 0.4 }}
            >
              <StatNumber>100k+</StatNumber>
              <StatLabel>Rozwiązanych Quizów</StatLabel>
            </StatCard>
          </StatsGrid>
          <div style={{ textAlign: 'center' }}>
            <CTAButton to={user ? "/quizzes" : "/register"}>
              {user ? "Rozpocznij naukę" : "Dołącz teraz"}
            </CTAButton>
          </div>
        </Container>
      </StatsSection>
    </HomeContainer>
  );
};

export default Home;
