import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { useTranslation } from '../context/TranslationContext';

const Hero = styled(motion.section)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 60vh;
  text-align: center;
  color: #333;
`;

const Title = styled.h1`
  font-size: 3rem;
  font-weight: 800;
  margin-bottom: 16px;
  background: linear-gradient(90deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const Subtitle = styled.p`
  font-size: 1.3rem;
  margin-bottom: 32px;
  color: #555;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  flex-wrap: wrap;
`;

const HeroButton = styled(Link)`
  padding: 16px 32px;
  border-radius: 10px;
  font-size: 1.1rem;
  font-weight: 600;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  text-decoration: none;
  box-shadow: 0 4px 16px rgba(102, 126, 234, 0.15);
  transition: all 0.3s;
  &:hover {
    background: linear-gradient(135deg, #764ba2 0%, #667eea 100%);
    transform: translateY(-3px) scale(1.04);
    box-shadow: 0 8px 32px rgba(102, 126, 234, 0.18);
  }
`;

const Home = () => {
  const { t } = useTranslation();

  return (
    <Hero initial={{ opacity: 0, y: 40 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.7 }}>
      <Title>QuizMaster</Title>
      <Subtitle>
        {t('Nowoczesna platforma e-learningowa do rozwiązywania i tworzenia quizów.')}<br />
        {t('Ucz się, sprawdzaj wiedzę i rywalizuj z innymi!')}<br />
        <span style={{ color: '#764ba2', fontWeight: 600 }}>{t('Dostępne po polsku i angielsku.')}</span>
      </Subtitle>
      <ButtonGroup>
        <HeroButton to="/quizzes">{t('Przeglądaj quizy')}</HeroButton>
        <HeroButton to="/create-quiz">{t('Utwórz własny quiz')}</HeroButton>
      </ButtonGroup>
    </Hero>
  );
};

export default Home;
