import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion';
import { useTranslation } from '../context/TranslationContext';

const QuizList = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [language, setLanguage] = useState('');
  const [category, setCategory] = useState('');
  const [difficulty, setDifficulty] = useState('');
  const [categories, setCategories] = useState([]);
  const { t, updateKey, currentLanguage, translateQuizList } = useTranslation();

  useEffect(() => {
    fetchQuizzes();
    fetchCategories();
    // eslint-disable-next-line
  }, [language, category, difficulty, updateKey]);

  const fetchQuizzes = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/quizzes', {
        params: { language, category, difficulty }
      });
      
      // Tłumacz quizy jeśli język nie jest polski
      let translatedQuizzes = res.data;
      if (currentLanguage !== 'pl') {
        translatedQuizzes = await translateQuizList(res.data, currentLanguage);
      }
      
      setQuizzes(translatedQuizzes);
      setError('');
    } catch (err) {
      setError(t('Błąd podczas pobierania quizów.'));
    }
    setLoading(false);
  };

  const fetchCategories = async () => {
    try {
      const res = await axios.get('/api/categories');
      setCategories(res.data);
    } catch (err) {
      setCategories([]);
    }
  };

  return (
    <motion.div className="container fade-in" initial={{ opacity: 0 }} animate={{ opacity: 1 }} key={updateKey}>
      <h2 style={{ margin: '32px 0 24px 0', textAlign: 'center' }}>{t('Dostępne quizy')}</h2>
      <div style={{ display: 'flex', gap: 16, flexWrap: 'wrap', marginBottom: 24, justifyContent: 'center' }}>
        <select className="form-select" value={language} onChange={e => setLanguage(e.target.value)} style={{ maxWidth: 220 }}>
          <option value="">{t('Wszystkie języki')}</option>
          <option value="pl">{t('Polski')}</option>
          <option value="en">{t('Angielski')}</option>
        </select>
        <select className="form-select" value={category} onChange={e => setCategory(e.target.value)} style={{ maxWidth: 220 }}>
          <option value="">{t('Wszystkie kategorie')}</option>
          {categories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
        <select className="form-select" value={difficulty} onChange={e => setDifficulty(e.target.value)} style={{ maxWidth: 220 }}>
          <option value="">{t('Wszystkie poziomy')}</option>
          <option value="easy">{t('Łatwy')}</option>
          <option value="medium">{t('Średni')}</option>
          <option value="hard">{t('Trudny')}</option>
        </select>
      </div>
      {loading ? (
        <div>{t('Ładowanie quizów...')}</div>
      ) : error ? (
        <div style={{ color: 'red' }}>{error}</div>
      ) : quizzes.length === 0 ? (
        <div>{t('Brak quizów do wyświetlenia.')}</div>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: 24, justifyContent: 'center' }}>
          {quizzes.map(quiz => (
            <motion.div
              className="card fade-in"
              key={quiz.id}
              whileHover={{ scale: 1.04 }}
              style={{ minWidth: 280, maxWidth: 340 }}
            >
              <h3 style={{ marginBottom: 8 }}>{quiz.title}</h3>
              <div style={{ color: '#764ba2', fontWeight: 500, marginBottom: 8 }}>{quiz.category}</div>
              <div style={{ marginBottom: 8 }}>{quiz.description}</div>
              <div style={{ fontSize: 14, color: '#888', marginBottom: 8 }}>
                {t('Autor')}: {quiz.author} | {t('Pytania')}: {quiz.questionCount}
              </div>
              <div style={{ fontSize: 14, color: '#888', marginBottom: 8 }}>
                {t('Poziom')}: {t(quiz.difficulty === 'easy' ? 'Łatwy' : quiz.difficulty === 'medium' ? 'Średni' : 'Trudny')} | {t('Język')}: {quiz.language}
              </div>
              <Link to={`/quizzes/${quiz.id}`} className="btn btn-primary" style={{ width: '100%' }}>
                {t('Zobacz quiz')}
              </Link>
            </motion.div>
          ))}
        </div>
      )}
    </motion.div>
  );
};

export default QuizList;
