import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { motion } from 'framer-motion';
import { FiTrash2 } from 'react-icons/fi';

const QuizDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [quiz, setQuiz] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [translating, setTranslating] = useState(false);
  const [deleting, setDeleting] = useState(false);

  useEffect(() => {
    fetchQuiz();
    // eslint-disable-next-line
  }, [id]);

  const fetchQuiz = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`/api/quizzes/${id}`);
      setQuiz(res.data);
      setError('');
    } catch (err) {
      setError('Nie znaleziono quizu.');
    }
    setLoading(false);
  };

  const handleStart = () => {
    navigate(`/quiz/${quiz.id}`);
  };

  const handleTranslate = async (targetLanguage) => {
    if (!user) {
      alert('Musisz być zalogowany, aby tłumaczyć quiz.');
      return;
    }
    setTranslating(true);
    try {
      const res = await axios.post(`/api/quizzes/${quiz.id}/translate`, { targetLanguage });
      navigate(`/quizzes/${res.data.id}`);
    } catch (err) {
      alert('Błąd tłumaczenia quizu.');
    }
    setTranslating(false);
  };

  const handleDelete = async () => {
    if (!window.confirm('Czy na pewno chcesz usunąć ten quiz? Tej operacji nie można cofnąć.')) {
      return;
    }

    setDeleting(true);
    try {
      await axios.delete(`/api/quizzes/${quiz.id}`);
      navigate('/my-quizzes');
    } catch (err) {
      alert(err.response?.data?.error || 'Błąd podczas usuwania quizu.');
    }
    setDeleting(false);
  };

  const isAuthor = user && quiz && quiz.author === user.username;

  return (
    <motion.div className="container fade-in" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
      {loading ? (
        <div>Ładowanie quizu...</div>
      ) : error ? (
        <div style={{ color: 'red' }}>{error}</div>
      ) : quiz ? (
        <div className="card" style={{ maxWidth: 700, margin: '40px auto' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: 16 }}>
            <div>
              <h2 style={{ marginBottom: 8 }}>{quiz.title}</h2>
              <div style={{ color: '#764ba2', fontWeight: 500, marginBottom: 8 }}>{quiz.category}</div>
            </div>
            {isAuthor && (
              <button 
                className="btn btn-danger" 
                onClick={handleDelete}
                disabled={deleting}
                style={{ display: 'flex', alignItems: 'center', gap: 6 }}
              >
                <FiTrash2 />
                {deleting ? 'Usuwanie...' : 'Usuń quiz'}
              </button>
            )}
          </div>
          
          <div style={{ marginBottom: 8 }}>{quiz.description}</div>
          <div style={{ fontSize: 14, color: '#888', marginBottom: 8 }}>
            Autor: {quiz.author} | Poziom: {quiz.difficulty} | Język: {quiz.language}
          </div>
          <div style={{ marginBottom: 16 }}>
            <b>Liczba pytań:</b> {quiz.questions.length}
          </div>
          <div style={{ marginBottom: 16 }}>
            <b>Pytania:</b>
            <ol style={{ marginTop: 8 }}>
              {quiz.questions.map(q => (
                <li key={q.id} style={{ marginBottom: 8 }}>
                  {q.question}
                </li>
              ))}
            </ol>
          </div>
          <div style={{ display: 'flex', gap: 16, flexWrap: 'wrap', marginTop: 16 }}>
            <button className="btn btn-primary" onClick={handleStart}>
              Rozpocznij quiz
            </button>
            {quiz.language === 'pl' && (
              <button className="btn btn-secondary" onClick={() => handleTranslate('en')} disabled={translating}>
                {translating ? 'Tłumaczenie...' : 'Przetłumacz na angielski'}
              </button>
            )}
            {quiz.language === 'en' && (
              <button className="btn btn-secondary" onClick={() => handleTranslate('pl')} disabled={translating}>
                {translating ? 'Tłumaczenie...' : 'Przetłumacz na polski'}
              </button>
            )}
          </div>
        </div>
      ) : null}
    </motion.div>
  );
};

export default QuizDetail;
