import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { motion } from 'framer-motion';

const Profile = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStats();
    // eslint-disable-next-line
  }, []);

  const fetchStats = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/user/stats');
      setStats(res.data);
    } catch {
      setStats(null);
    }
    setLoading(false);
  };

  return (
    <motion.div className="container fade-in" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
      <div className="card" style={{ maxWidth: 500, margin: '40px auto' }}>
        <h2 style={{ marginBottom: 16 }}>Twój profil</h2>
        <div style={{ marginBottom: 16 }}>
          <b>Nazwa użytkownika:</b> {user?.username}
        </div>
        <div style={{ marginBottom: 16 }}>
          <b>E-mail:</b> {user?.email}
        </div>
        {loading ? (
          <div>Ładowanie statystyk...</div>
        ) : stats ? (
          <>
            <div style={{ marginBottom: 8 }}>
              <b>Quizy rozwiązane:</b> {stats.totalQuizzes}
            </div>
            <div style={{ marginBottom: 8 }}>
              <b>Średni wynik:</b> {stats.averageScore}
            </div>
            <div style={{ marginBottom: 8 }}>
              <b>Łączny czas rozwiązywania:</b> {stats.totalTime} sekund
            </div>
            <div style={{ marginBottom: 8 }}>
              <b>Unikalnych quizów ukończonych:</b> {stats.quizzesCompleted}
            </div>
          </>
        ) : (
          <div>Brak statystyk do wyświetlenia.</div>
        )}
      </div>
    </motion.div>
  );
};

export default Profile;
